import React, {Component} from 'react'; 
import '../components/signup.css';
import ConfirmButton from '../components/confirmButton';


//refactor into a controlled component 
//on click, go grey
//or on click, disappear
//on hover for add, go blue
//Make long for add button

class SignUp extends Component{

    constructor(props){
        super(props);
            this.state = {
                email: "",
                copied: false
            };
        }
    changeHandler = e => {
        this.setState({email: e.target.value});
    }

    submitHandler = e => {
        e.preventDefault()
        console.log("should input test data" +this.state)
        fetch('/api/email', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                "Email": this.state.email
            })
            })
            this.setState({copied: true});

    }

    render() {
        const {name} = this.state
        return (
            <div className = "FormContainer"> 
            <div className = "Title"> Let’s build Peerlift together ✨  </div>
            <div className = "Text">We do this thing where we ask for your advice, and we share new features and ideas over email.  We’re also students and hate spam too.   Unsubscribe whenever you want. </div>
            <form onSubmit= {this.submitHandler} className = "Form">
                <input className = "FormInput" type = "text" placeholder = "Your email address" value ={this.state.email} onChange = {this.changeHandler}>
                </input>
                <button type = "submit" className = "SignUpButton" onClick = {this.myClick}> Sign up
                </button>
            </form>
            {this.state.copied ? 
                    <React.Fragment>
                     <ConfirmButton
                     text = "Sent to Peerlift!"
                     visible = {false}
                     ></ConfirmButton>
                      </React.Fragment>
                     : null}
            </div>
        );
    }
  }

  export default SignUp;
