import React, {Component} from 'react'; 
import '../components/OppBox.css';
import '../components/filters.css';
import Filters from "../components/filters";
import OppBox from '../components/OppBox';
import AddOpp from '../components/addOpp.js';

//need to import contacts to here
// //This is a constructor for a component 
class Opps extends Component{

  constructor(props){
    super(props);
    this.state = {
      contacts: [],
      contacts_const: [],
      on: false
    };
     // This binding is necessary to make `this` work in the callback-- added from tut
     this.handleClickTag = this.handleClickTag.bind(this);
  }

  componentDidMount() {
    fetch('http://localhost:9000/users')
      .then(res => res.json())
      .then((data) => {
        //data is an array, let's sort it by closest to current date
        data.sort(function(a,b){
          var currDate = new Date();
          //create date variables for each one
          var c = new Date(a.FilterDate);
          var d = new Date(b.FilterDate);

          var e = Math.abs(currDate-c);
          var f = Math.abs(currDate-d);
          return e-f;
        })
        this.setState({ contacts: data })
        //give constant a value
        this.setState({ contacts_const: data })
      })
      .catch(console.log)
  }

        //filter tags
        handleClickTag(word) {
        //set the state of contacts at the end
        if (!this.state.on){
            //const word = "STEM";
            //filter from contacts_consc to ensure always from fullset
            const array = this.state.contacts_const;
            const arrayTwo = array.filter(item => {
                //console.log(item.Tags)
                return item.Tags.includes(word);
              })    
            this.setState({contacts:arrayTwo})
            this.setState({on:!this.state.on})
            console.log(this.state.on)
        }
        else{
            this.setState({contacts:this.state.contacts_const})
            this.setState({on:!this.state.on})
        }
    }
    //for year
    handleClickYear(word) {
        if (!this.state.on){
            //take the modified contact list
            const array = this.state.contacts;
            const arrayTwo = array.filter(item => {
                return item.Grade.includes(word);
              })    
            this.setState({contacts:arrayTwo})
            this.setState({on:!this.state.on})
        }
        else{
            this.setState({contacts:this.state.contacts_const})
            this.setState({on:!this.state.on})
        }
    }

  render() {
    //console.log("test"+this.state.contacts)
    //we want to reset value to normal
    var word;
    return(
        <div className = "filter-opp-wrapper">
          <div className = "full-filter-wrapper"> 
          <div className = "filter-wrapper">
            <div className = "header">Filter </div>

            <div className = "grade-wrapper">
            <div className = "title">Grade </div>
            <button className={this.state.on ? "button-clicked": "filter-button"} onClick={() => this.handleClickYear(word = "9")}> 9</button>
            <button className = "filter-button" onClick={() => this.handleClickYear(word = "10")}> 10 </button>
            <button className = "filter-button" onClick={() => this.handleClickYear(word = "11")}>11 </button>
            <button className = "filter-button" onClick={() => this.handleClickYear(word = "12")}>12 </button>
            </div>

            <div className = "title"> Tags</div>
            <div className = "tags"> 
            <button className = "filter-button" onClick={() => this.handleClickTag(word = "STEM")}> STEM</button>
            <button className = "filter-button" onClick={() => this.handleClickTag(word = "low-income")}> Low-income </button>
            <button className = "filter-button" onClick={() => this.handleClickTag(word = "diversity")}>Diversity </button>
            <button className = "filter-button" onClick={() => this.handleClickTag(word = "service")}>Service </button>
            </div>
            </div>


          </div>

          <div className = "add-box-wrapper"> 

          <AddOpp></AddOpp>
          <div className = "opp-box-wrapper"> 

            <OppBox contacts = {this.state.contacts}> </OppBox> 
        </div>
        </div>
        </div>
    );
}
}

export default Opps;


